model:model:
$identify("org/mathdox/formulaeditor/modules//.js");

$require("org/mathdox/formulaeditor/semantics/MultaryOperation.js");
$require("org/mathdox/formulaeditor/presentation/Superscript.js");
$require("org/mathdox/formulaeditor/parsing/openmath/OpenMathParser.js");
$require("org/mathdox/formulaeditor/parsing/expression/ExpressionContextParser.js");
$require("org/mathdox/formulaeditor/semantics/Keyword.js");

$main(function(){

  var mathmlSymbol= [ "", "", ""];

  if ("" !== "") {
    mathmlSymbol[0] = "<mo></mo>";
  }
  if ("" !== "") {
    mathmlSymbol[2] = "<mo></mo>";
  }

  var symbol =  {
    onscreen : ["","",""],
    openmath : "<OMS cd='' name=''/>",
    mathml   : mathmlSymbol
  };

  /**
   * Defines a semantic tree node that represents a unary minus.
   */
  org.mathdox.formulaeditor.semantics. =
    $extend(org.mathdox.formulaeditor.semantics.MultaryOperation, {


      symbol : {
        onscreen : symbol.onscreen,
        openmath : symbol.openmath,
        mathml   : mathmlSymbol
      },

      precedence : 

    });

  /**
   * Extend the OpenMathParser object with parsing code for arith1.unary_minus.
   */
  org.mathdox.formulaeditor.parsing.openmath.OpenMathParser =
    $extend(org.mathdox.formulaeditor.parsing.openmath.OpenMathParser, {

      /**
      * Returns a unary minus object based on the OpenMath node.
      */
      handle : function(node) {

        var operand = this.handle(node.childNodes.item(1));
        return new org.mathdox.formulaeditor.semantics.(operand);

      }

    });

  org.mathdox.formulaeditor.parsing.openmath.KeywordList["__"] = new org.mathdox.formulaeditor.semantics.Keyword("", "", symbol, "unary");

  /**
   * Add the parsing code for unary symbol.
   */
  var semantics = org.mathdox.formulaeditor.semantics;
  var pG = new org.mathdox.parsing.ParserGenerator();

  var rulesEnter = [];
  var positionEnter = 0;
  if ("" !== "") {
    rulesEnter.push(pG.literal(""));
    positionEnter++;
  }
  rulesEnter.push(pG.rule("expression"));
  if ("" !== "") {
    rulesEnter.push(pG.literal(""));
  }

  if (( ""  === ""  ) &&
      ( "" === "" )) {
    // only one expression, same on screen
    org.mathdox.formulaeditor.parsing.expression.ExpressionContextParser.addFunction( 
      function(context) { return {

        // expression =  | super.expression
        expression : function() {
          var parent = arguments.callee.parent;
          pG.alternation(
            pG.rule(""),
            parent.expression).apply(this, arguments);
        },

        //  = "" expression ""
         :
          pG.transform(
            pG.concatenation.apply(pG, rulesEnter),
            function(result) {
              return new semantics.(result[positionEnter]);
            }
          )
      };
    });
  } else { // allow alternative as displayed on the screen
    var rulesScreen = [];
    var positionScreen = 0;
    if ("" !== "") {
      rulesScreen.push(pG.literal(""));
      positionScreen++;
    }
    rulesScreen.push(pG.rule("expression"));
    if ("" !== "") {
      rulesScreen.push(pG.literal(""));
    }

    org.mathdox.formulaeditor.parsing.expression.ExpressionContextParser.addFunction( 
      function(context) { return {

        // expression =  | super.expression
        expression : function() {
          var parent = arguments.callee.parent;
          pG.alternation(
            pG.rule(""),
            pG.rule("alt"),
            parent.expression).apply(this, arguments);
        },

        //  = "" expression ""
         :
          pG.transform(
            pG.concatenation.apply(pG, rulesEnter),
            function(result) {
              return new semantics.(result[positionEnter]);
            }
          ),

        // alt = "" expression ""
        alt :
          pG.transform(
            pG.concatenation.apply(pG, rulesScreen),
            function(result) {
              return new semantics.(result[positionScreen]);
            }
          )
       };
     });
   }

});
